// ConfiguraciÃ³n ESPECÃFICA para proyecto Cortes en Mac
const baseProjectPath = '/data/cortes';
const cortesAppPath = '/data/cortes/Cortes';
const contextPath = '/data/cortes/cortes-context';
const macProjectPath = '/Users/molinesmac/Documents/Cortes';
const macContextPath = '/Users/molinesmac/Documents/Cortes/cortes-context';

const config = {
  project_path: baseProjectPath,
  cortes_app_path: cortesAppPath,
  context_path: contextPath,
  mac_project_path: macProjectPath,
  mac_context_path: macContextPath,
  detected_automatically: true,
  real_path_found: true,
  timestamp: new Date().toISOString(),
  container_id: process.env.HOSTNAME || 'n8n-container',
  workflow_version: '8.0.0-production-ready'
};

console.log('âœ… Cortes project paths configured:', config);
