// ANÃLISIS ROBUSTO DE ESTRUCTURA DEL PROYECTO CORTES
const projectFiles = items[0]?.json?.stdout?.trim() || '';
const pathConfig = items[1]?.json || {};

const analysis = {
  timestamp: new Date().toISOString(),
  project_name: 'Cortes',
  project_path: pathConfig.project_path || '/data/cortes',
  cortes_app_path: pathConfig.cortes_app_path || '/data/cortes/Cortes',
  project_detected: pathConfig.detected_automatically || false,
  scan_successful: !projectFiles.includes('no_project_files_found') && !projectFiles.includes('project_path_needs_volume_mapping'),
  files_found: projectFiles !== 'no_project_files_found' && projectFiles.length > 0,
  total_files: 0,
  categories: {
    swift_files: 0,
    js_files: 0,
    config_files: 0,
    documentation: 0,
    xcode_projects: 0,
    info_plists: 0
  },
  cortes_structure: {
    main_app_files: [],
    view_files: [],
    model_files: [],
    config_files: [],
    documentation_files: []
  },
  project_type_detected: 'unknown',
  cortes_specific_insights: {
    ios_project: false,
    swiftui_detected: false,
    package_manager_detected: false,
    documentation_quality: 'unknown'
  }
};

// Parse files robustly
if (analysis.scan_successful && analysis.files_found) {
  const fileList = projectFiles.split('\n')
    .filter(f => f.length > 0 && !f.includes('Scanning Cortes project files'))
    .map(f => f.trim())
    .filter(f => f.length > 0);

  analysis.total_files = fileList.length;

  // Count by categories
  analysis.categories.swift_files = fileList.filter(f => f.endsWith('.swift')).length;
  analysis.categories.js_files = fileList.filter(f => f.match(/\.(js|jsx|ts|tsx)$/)).length;
  analysis.categories.config_files = fileList.filter(f => f.match(/(package\.json|Package\.swift|Package\.resolved)/)).length;
  analysis.categories.documentation = fileList.filter(f => f.endsWith('.md')).length;
  analysis.categories.xcode_projects = fileList.filter(f => f.match(/\.(xcodeproj|xcworkspace)$/)).length;
  analysis.categories.info_plists = fileList.filter(f => f.includes('Info.plist')).length;

  // Classify Cortes-specific files
  analysis.cortes_structure.main_app_files = fileList.filter(f => 
    f.includes('/CortesApp') || f.includes('CortesApp.swift') || f.includes('main.swift')
  );
  analysis.cortes_structure.view_files = fileList.filter(f => 
    f.includes('View.swift') || f.includes('/Views/') || f.includes('ViewController')
  );
  analysis.cortes_structure.model_files = fileList.filter(f => 
    f.includes('Model.swift') || f.includes('/Models/') || f.includes('Entity')
  );
  analysis.cortes_structure.config_files = fileList.filter(f => 
    f.match(/(package\.json|Package\.swift|Package\.resolved|Info\.plist)/)
  );
  analysis.cortes_structure.documentation_files = fileList.filter(f => 
    f.includes('/Documentation/') || f.includes('/docs/') || f.endsWith('.md')
  );

  // Detect project type and Cortes-specific insights
  if (analysis.categories.swift_files > 0 && analysis.categories.xcode_projects > 0) {
    analysis.project_type_detected = 'iOS_Native_Swift_Cortes';
    analysis.cortes_specific_insights.ios_project = true;
  } else if (analysis.categories.swift_files > 0) {
    analysis.project_type_detected = 'Swift_Package_Cortes';
    analysis.cortes_specific_insights.package_manager_detected = true;
  } else if (analysis.categories.js_files > 0) {
    analysis.project_type_detected = 'Web_Component_Cortes';
  } else if (analysis.total_files > 0) {
    analysis.project_type_detected = 'Mixed_Cortes_Project';
  }

  // SwiftUI detection
  const swiftUIIndicators = fileList.filter(f => 
    f.includes('SwiftUI') || f.includes('ContentView') || f.includes('@State') ||
    f.includes('View.swift') && analysis.categories.swift_files > 5
  );
  analysis.cortes_specific_insights.swiftui_detected = swiftUIIndicators.length > 0;

  // Documentation quality assessment
  if (analysis.categories.documentation > 5) {
    analysis.cortes_specific_insights.documentation_quality = 'excellent';
  } else if (analysis.categories.documentation > 2) {
    analysis.cortes_specific_insights.documentation_quality = 'good';
  } else if (analysis.categories.documentation > 0) {
    analysis.cortes_specific_insights.documentation_quality = 'basic';
  } else {
    analysis.cortes_specific_insights.documentation_quality = 'minimal';
  }
}

console.log('âœ… Cortes project analysis completed:', {
  files_found: analysis.files_found,
  total_files: analysis.total_files,
  project_type: analysis.project_type_detected,
  ios_project: analysis.cortes_specific_insights.ios_project
});

return [{ json: analysis }];

// ANÃLISIS ROBUSTO DE GIT INTELLIGENCE PARA CORTES
const gitHistory = items[0]?.json?.stdout || 'no_git_repository_found';
const gitStats = items[1]?.json?.stdout || 'no_git_stats_available';

const gitIntelligence = {
  timestamp: new Date().toISOString(),
  project_name: 'Cortes',
  git_available: !gitHistory.includes('no_git_repository_found'),
  git_status_clean: gitHistory.includes('Git analysis for Cortes:'),
  development_velocity: {
    commits_30_days: 0,
    commits_14_days: 0,
    active_developers: [],
    commit_frequency: 'unknown',
    development_pace: 'unknown'
  },
  commit_patterns: {
    common_messages: [],
    commit_types: {},
    recent_activity: false,
    cortes_specific_commits: []
  },
  hot_spots: {
    most_changed_files: [],
    refactor_candidates: [],
    cortes_core_changes: []
  },
  cortes_development_insights: {
    core_feature_development: 'unknown',
    ui_development_activity: 'unknown',
    backend_development_activity: 'unknown',
    testing_development_activity: 'unknown'
  },
  insights: {
    development_health: 'unknown',
    code_stability: 'unknown',
    team_collaboration: 'unknown',
    project_momentum: 'unknown'
  }
};

if (gitIntelligence.git_available) {
  // Parse Git history more robustly
  const historyLines = gitHistory.split('\n')
    .filter(line => line.includes('|') && line.match(/^[a-f0-9]+\|/));

  gitIntelligence.development_velocity.commits_30_days = historyLines.length;
  gitIntelligence.commit_patterns.recent_activity = historyLines.length > 0;

  if (historyLines.length > 0) {
    // Parse commit data
    const commitData = historyLines.map(line => {
      const parts = line.split('|');
      return {
        hash: parts[0]?.trim(),
        author: parts[1]?.trim(),
        date: parts[2]?.trim(),
        message: parts.slice(3).join('|').trim()
      };
    }).filter(commit => commit.hash && commit.author);

    // Analyze developers
    const developers = [...new Set(commitData.map(c => c.author))];
    gitIntelligence.development_velocity.active_developers = developers;

    // Analyze Cortes-specific commit patterns
    const cortesCommits = commitData.filter(c => 
      c.message.toLowerCase().includes('cortes') ||
      c.message.toLowerCase().includes('client') ||
      c.message.toLowerCase().includes('session') ||
      c.message.toLowerCase().includes('analytics')
    );
    gitIntelligence.commit_patterns.cortes_specific_commits = cortesCommits.slice(0, 10);

    // Analyze commit patterns
    const messages = commitData.map(c => c.message.toLowerCase());
    const commonWords = {};
    messages.forEach(msg => {
      const words = msg.split(' ').filter(w => w.length > 3 && !['the', 'and', 'for', 'with'].includes(w));
      words.forEach(word => {
        commonWords[word] = (commonWords[word] || 0) + 1;
      });
    });

    gitIntelligence.commit_patterns.common_messages = Object.entries(commonWords)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 15)
      .map(([word, count]) => ({ word, count }));

    // Detect commit types for Cortes
    const commitTypes = {
      feature: messages.filter(m => m.includes('feat') || m.includes('add') || m.includes('implement') || m.includes('new')).length,
      fix: messages.filter(m => m.includes('fix') || m.includes('bug') || m.includes('resolve') || m.includes('patch')).length,
      refactor: messages.filter(m => m.includes('refactor') || m.includes('clean') || m.includes('optimize')).length,
      test: messages.filter(m => m.includes('test') || m.includes('spec') || m.includes('coverage')).length,
      docs: messages.filter(m => m.includes('doc') || m.includes('readme') || m.includes('comment')).length,
      ui: messages.filter(m => m.includes('ui') || m.includes('view') || m.includes('design') || m.includes('style')).length,
      backend: messages.filter(m => m.includes('api') || m.includes('server') || m.includes('database') || m.includes('core')).length
    };

    gitIntelligence.commit_patterns.commit_types = commitTypes;

    // Cortes-specific development insights
    gitIntelligence.cortes_development_insights = {
      core_feature_development: commitTypes.feature > 5 ? 'high' : commitTypes.feature > 2 ? 'moderate' : 'low',
      ui_development_activity: commitTypes.ui > 3 ? 'high' : commitTypes.ui > 1 ? 'moderate' : 'low',
      backend_development_activity: commitTypes.backend > 3 ? 'high' : commitTypes.backend > 1 ? 'moderate' : 'low',
      testing_development_activity: commitTypes.test > 2 ? 'good' : commitTypes.test > 0 ? 'minimal' : 'none'
    };

    // Calculate last 14 days for more recent activity
    const fourteenDaysAgo = new Date();
    fourteenDaysAgo.setDate(fourteenDaysAgo.getDate() - 14);

    const last14Days = commitData.filter(c => {
      const commitDate = new Date(c.date);
      return commitDate >= fourteenDaysAgo;
    });

    gitIntelligence.development_velocity.commits_14_days = last14Days.length;

    // Determine commit frequency and pace
    if (last14Days.length > 20) {
      gitIntelligence.development_velocity.commit_frequency = 'very_high';
      gitIntelligence.development_velocity.development_pace = 'blazing';
    } else if (last14Days.length > 10) {
      gitIntelligence.development_velocity.commit_frequency = 'high';
      gitIntelligence.development_velocity.development_pace = 'fast';
    } else if (last14Days.length > 5) {
      gitIntelligence.development_velocity.commit_frequency = 'moderate';
      gitIntelligence.development_velocity.development_pace = 'steady';
    } else if (last14Days.length > 0) {
      gitIntelligence.development_velocity.commit_frequency = 'low';
      gitIntelligence.development_velocity.development_pace = 'slow';
    } else {
      gitIntelligence.development_velocity.commit_frequency = 'inactive';
      gitIntelligence.development_velocity.development_pace = 'stagnant';
    }
  }

  // Analyze git stats for hot spots
  if (!gitStats.includes('no_git_stats_available')) {
    const statsLines = gitStats.split('\n')
      .filter(line => line.length > 0 && !line.includes('Git statistics analysis:'));

    const fileChanges = {};
    let currentCommit = null;

    statsLines.forEach(line => {
      if (line.includes('|') && !line.match(/^\d+\s+\d+\s+/)) {
        currentCommit = line.split('|')[0]?.trim();
      } else if (line.match(/^\d+\s+\d+\s+/) && currentCommit) {
        const parts = line.trim().split(/\s+/);
        if (parts.length >= 3) {
          const added = parseInt(parts[0]) || 0;
          const removed = parseInt(parts[1]) || 0;
          const fileName = parts.slice(2).join(' ');

          if (!fileChanges[fileName]) {
            fileChanges[fileName] = { changes: 0, added: 0, removed: 0 };
          }

          fileChanges[fileName].changes++;
          fileChanges[fileName].added += added;
          fileChanges[fileName].removed += removed;
        }
      }
    });

    // Identify hot spots
    const hotSpots = Object.entries(fileChanges)
      .sort(([,a], [,b]) => b.changes - a.changes)
      .slice(0, 15)
      .map(([file, stats]) => ({ file, ...stats }));

    gitIntelligence.hot_spots.most_changed_files = hotSpots;

    // Identify Cortes core changes
    const cortesCore = hotSpots.filter(item => 
      item.file.includes('Cortes') || 
      item.file.includes('Client') || 
      item.file.includes('Session') ||
      item.file.includes('Analytics')
    );
    gitIntelligence.hot_spots.cortes_core_changes = cortesCore;

    // Identify refactor candidates
    const refactorCandidates = hotSpots
      .filter(item => item.changes > 5 && (item.added + item.removed) > 100)
      .map(item => ({
        file: item.file,
        churn_score: item.changes * (item.added + item.removed),
        reason: 'High change frequency with significant modifications',
        priority: item.file.includes('Cortes') ? 'high' : 'medium'
      }));

    gitIntelligence.hot_spots.refactor_candidates = refactorCandidates;
  }

  // Generate enhanced insights
  gitIntelligence.insights = {
    development_health: gitIntelligence.development_velocity.commit_frequency,
    team_size: gitIntelligence.development_velocity.active_developers.length,
    code_stability: gitIntelligence.hot_spots.refactor_candidates.length < 3 ? 'stable' : 'needs_attention',
    project_momentum: gitIntelligence.development_velocity.commits_14_days > 0 ? 'active' : 'stagnant',
    team_collaboration: gitIntelligence.development_velocity.active_developers.length > 1 ? 'collaborative' : 'solo'
  };
} else {
  gitIntelligence.insights = {
    development_health: 'no_git_repository',
    code_stability: 'unknown',
    team_size: 0,
    project_momentum: 'unknown',
    team_collaboration: 'unknown'
  };
}

console.log('âœ… Cortes Git intelligence completed:', {
  git_available: gitIntelligence.git_available,
  commits_30_days: gitIntelligence.development_velocity.commits_30_days,
  development_pace: gitIntelligence.development_velocity.development_pace
});

return [{ json: gitIntelligence }];

// ANÃLISIS ROBUSTO DE TESTING INTELLIGENCE PARA CORTES
const testFiles = items[0]?.json?.stdout || 'no_test_files_found';

const testingIntelligence = {
  timestamp: new Date().toISOString(),
  project_name: 'Cortes',
  scan_successful: !testFiles.includes('no_test_files_found'),
  tests_found: testFiles !== 'no_test_files_found' && testFiles.length > 0,
  test_coverage: {
    total_test_files: 0,
    swift_test_files: 0,
    js_test_files: 0,
    ui_test_files: 0,
    unit_test_files: 0
  },
  cortes_testing_patterns: {
    client_tests: [],
    session_tests: [],
    analytics_tests: [],
    ui_tests: [],
    integration_tests: []
  },
  testing_frameworks: {
    swift_testing_framework: 'XCTest',
    js_testing_framework: 'unknown',
    ui_testing_framework: 'XCUITest'
  },
  cortes_test_insights: {
    client_management_coverage: 'unknown',
    session_management_coverage: 'unknown',
    analytics_coverage: 'unknown',
    ui_coverage: 'unknown',
    monetization_coverage: 'unknown'
  },
  insights: {
    test_coverage_level: 'none',
    testing_maturity: 'none',
    recommendation: 'Initialize comprehensive Cortes testing setup',
    deployment_confidence: 'low'
  }
};

if (testingIntelligence.scan_successful && testingIntelligence.tests_found) {
  const testFileList = testFiles.split('\n')
    .filter(f => f.length > 0 && !f.includes('Scanning Cortes test files'))
    .map(f => f.trim())
    .filter(f => f.length > 0);

  testingIntelligence.test_coverage.total_test_files = testFileList.length;
  testingIntelligence.test_coverage.swift_test_files = testFileList.filter(f => 
    f.endsWith('.swift') && (f.includes('Test') || f.includes('Tests'))
  ).length;
  testingIntelligence.test_coverage.js_test_files = testFileList.filter(f => 
    f.match(/\.(test|spec)\.(js|ts|jsx|tsx)$/)
  ).length;
  testingIntelligence.test_coverage.ui_test_files = testFileList.filter(f => 
    f.includes('UITest') || f.includes('UITests')
  ).length;
  testingIntelligence.test_coverage.unit_test_files = testFileList.filter(f => 
    f.includes('UnitTest') || (f.includes('Test') && !f.includes('UITest'))
  ).length;

  // Analyze Cortes-specific test patterns
  testingIntelligence.cortes_testing_patterns = {
    client_tests: testFileList.filter(f => 
      f.toLowerCase().includes('client') && (f.includes('Test') || f.includes('Tests'))
    ),
    session_tests: testFileList.filter(f => 
      f.toLowerCase().includes('session') && (f.includes('Test') || f.includes('Tests'))
    ),
    analytics_tests: testFileList.filter(f => 
      f.toLowerCase().includes('analytics') && (f.includes('Test') || f.includes('Tests'))
    ),
    ui_tests: testFileList.filter(f => 
      f.includes('UITest') || (f.includes('View') && f.includes('Test'))
    ),
    integration_tests: testFileList.filter(f => 
      f.toLowerCase().includes('integration') && (f.includes('Test') || f.includes('Tests'))
    )
  };

  // Assess Cortes-specific test coverage
  testingIntelligence.cortes_test_insights = {
    client_management_coverage: testingIntelligence.cortes_testing_patterns.client_tests.length > 3 ? 'good' : 
                               testingIntelligence.cortes_testing_patterns.client_tests.length > 0 ? 'basic' : 'none',
    session_management_coverage: testingIntelligence.cortes_testing_patterns.session_tests.length > 3 ? 'good' : 
                                testingIntelligence.cortes_testing_patterns.session_tests.length > 0 ? 'basic' : 'none',
    analytics_coverage: testingIntelligence.cortes_testing_patterns.analytics_tests.length > 2 ? 'good' : 
                       testingIntelligence.cortes_testing_patterns.analytics_tests.length > 0 ? 'basic' : 'none',
    ui_coverage: testingIntelligence.cortes_testing_patterns.ui_tests.length > 5 ? 'good' : 
                testingIntelligence.cortes_testing_patterns.ui_tests.length > 2 ? 'basic' : 'none',
    monetization_coverage: testFileList.filter(f => 
      f.toLowerCase().includes('premium') || f.toLowerCase().includes('subscription') || f.toLowerCase().includes('payment')
    ).length > 0 ? 'basic' : 'none'
  };

  // Determine test coverage level and maturity for Cortes
  const totalTests = testFileList.length;
  const coreTestsCount = testingIntelligence.cortes_testing_patterns.client_tests.length + 
                        testingIntelligence.cortes_testing_patterns.session_tests.length + 
                        testingIntelligence.cortes_testing_patterns.analytics_tests.length;

  if (totalTests > 25 && coreTestsCount > 8) {
    testingIntelligence.insights.test_coverage_level = 'excellent';
    testingIntelligence.insights.testing_maturity = 'enterprise';
    testingIntelligence.insights.recommendation = 'Maintain excellent test coverage and consider performance testing';
    testingIntelligence.insights.deployment_confidence = 'very_high';
  } else if (totalTests > 15 && coreTestsCount > 5) {
    testingIntelligence.insights.test_coverage_level = 'good';
    testingIntelligence.insights.testing_maturity = 'mature';
    testingIntelligence.insights.recommendation = 'Expand testing for edge cases and premium features';
    testingIntelligence.insights.deployment_confidence = 'high';
  } else if (totalTests > 8 && coreTestsCount > 2) {
    testingIntelligence.insights.test_coverage_level = 'moderate';
    testingIntelligence.insights.testing_maturity = 'developing';
    testingIntelligence.insights.recommendation = 'Focus on core Cortes functionality testing';
    testingIntelligence.insights.deployment_confidence = 'moderate';
  } else if (totalTests > 3) {
    testingIntelligence.insights.test_coverage_level = 'basic';
    testingIntelligence.insights.testing_maturity = 'minimal';
    testingIntelligence.insights.recommendation = 'Prioritize client and session management tests';
    testingIntelligence.insights.deployment_confidence = 'low';
  } else if (totalTests > 0) {
    testingIntelligence.insights.test_coverage_level = 'minimal';
    testingIntelligence.insights.testing_maturity = 'basic';
    testingIntelligence.insights.recommendation = 'Implement basic test suite for core Cortes features';
    testingIntelligence.insights.deployment_confidence = 'very_low';
  }

  // Detect testing frameworks
  const hasJestPattern = testFileList.some(f => f.includes('.test.') || f.includes('.spec.'));
  if (hasJestPattern) {
    testingIntelligence.testing_frameworks.js_testing_framework = 'Jest/Testing Library';
  }
} else {
  testingIntelligence.insights = {
    test_coverage_level: 'none',
    testing_maturity: 'none',
    recommendation: 'CRITICAL: Implement comprehensive test suite for Cortes before production deployment',
    deployment_confidence: 'not_recommended'
  };
}

console.log('âœ… Cortes testing intelligence completed:', {
  tests_found: testingIntelligence.tests_found,
  total_test_files: testingIntelligence.test_coverage.total_test_files,
  deployment_confidence: testingIntelligence.insights.deployment_confidence
});

return [{ json: testingIntelligence }];

// ANÃLISIS ROBUSTO DE PERFORMANCE INTELLIGENCE PARA CORTES
const assetData = items[0]?.json?.stdout || 'no_assets_found';

const performanceIntelligence = {
  timestamp: new Date().toISOString(),
  project_name: 'Cortes',
  scan_successful: !assetData.includes('no_assets_found'),
  assets_found: assetData !== 'no_assets_found' && assetData.length > 0,
  asset_analysis: {
    total_assets: 0,
    large_assets: [],
    total_asset_size_kb: 0,
    asset_types: {},
    cortes_specific_assets: {
      app_icons: [],
      ui_assets: [],
      profile_images: [],
      marketing_assets: []
    },
    optimization_opportunities: []
  },
  cortes_performance_metrics: {
    app_store_readiness: 'unknown',
    ios_optimization_score: 100,
    user_experience_score: 100,
    monetization_impact: 'minimal'
  },
  optimization_recommendations: {
    immediate_actions: [],
    priority_focus: 'code_optimization',
    estimated_savings: '0MB',
    cortes_specific_optimizations: []
  }
};

if (performanceIntelligence.scan_successful && performanceIntelligence.assets_found) {
  const assetLines = assetData.split('\n')
    .filter(line => line.length > 0 && !line.includes('Analyzing Cortes assets') && line.includes('/'))
    .map(line => line.trim());

  performanceIntelligence.asset_analysis.total_assets = assetLines.length;

  const assets = [];
  let totalSizeKB = 0;
  const assetTypes = {};

  assetLines.forEach(line => {
    // Parse du output: "1.2M  /path/to/file.png"
    const match = line.match(/^([0-9.]+)([KMGT]?)\s+(.+)$/);
    if (match) {
      const [, sizeStr, unit, filePath] = match;
      const size = parseFloat(sizeStr);

      // Convert to KB
      let sizeKB = size;
      switch (unit.toUpperCase()) {
        case 'M': sizeKB = size * 1024; break;
        case 'G': sizeKB = size * 1024 * 1024; break;
        case 'T': sizeKB = size * 1024 * 1024 * 1024; break;
        case 'K': sizeKB = size; break;
        default: sizeKB = size / 1024; break; // Assume bytes
      }

      const fileName = filePath.split('/').pop();
      const extension = fileName?.split('.').pop()?.toLowerCase() || 'unknown';

      // Count by type
      assetTypes[extension] = (assetTypes[extension] || 0) + 1;

      const assetInfo = {
        path: filePath,
        name: fileName,
        extension,
        sizeKB: Math.round(sizeKB * 100) / 100,
        sizeDisplay: sizeStr + unit,
        category: ['png', 'jpg', 'jpeg', 'heic', 'webp'].includes(extension) ? 'image' : 
                 ['mp4', 'mov'].includes(extension) ? 'video' : 
                 ['pdf'].includes(extension) ? 'document' : 'other'
      };

      assets.push(assetInfo);
      totalSizeKB += sizeKB;
    }
  });

  performanceIntelligence.asset_analysis.total_asset_size_kb = Math.round(totalSizeKB * 100) / 100;
  performanceIntelligence.asset_analysis.asset_types = assetTypes;

  // Classify Cortes-specific assets
  performanceIntelligence.asset_analysis.cortes_specific_assets = {
    app_icons: assets.filter(a => 
      a.name.toLowerCase().includes('icon') || 
      a.name.toLowerCase().includes('logo') ||
      a.path.includes('AppIcon')
    ),
